define('controllers/chat_controller',[
		'./components/socket_client'
	, '../models/private_chat'
	, '../models/j'
	], function(SocketClient, PrivateChat, j) {

	SocketClient.lon('chat.message_result', function(e, message, next) {
		
		// Check if activity exist
		if (j.user.activities.get(message.activityId)) {
			j.user.activities.get(message.activityId).messages.add(message);
		} else {
			// Get activity 
			var activity = j.group.getActivity((message.activityId));
		
			// The activity is a private chat
			if (!activity){
				j.group.findUserById(message.userId, function(user) {
					if (user) { 
						j.user.addActivity(new PrivateChat({
							_id		:	message.activityId,
							name	: user.get('username')
						}));
						j.user.activities.get(message.activityId).messages.add(message);
						next();
					}					
				});
				
			} else {
				j.user.addActivity(activity);
				j.user.activities.get(message.activityId).messages.add(message);
				next();
			}	
		} 
		
	});

	j.lon('ui.chat.message', function(e, message, next) {
		// When a message is sent, add it to local messages to be displayed
		var activity = j.user.activities.get(message.activityId);
		var localMessage = _.extend({}, message);
		localMessage.senderUser = j.user.toJSON();
		activity.messages.add(localMessage);
		// If the activity is a private chat, add users ids to the message
		if (activity.get('type') == 'PrivateChat') {
			message.users = new Array();
			activity.users.each(function(user) {
				message.users.push(user.id);
			});
		}
		next();

	});

	j.lon('ui.private_chat.request', function(e, message, next) {
		// First check if there are any private chat windows with this user,
		// if there are none ...
		j.group.findUserById(message.targetUserId, function(user) {
			var activityId = j.user.getUuid();
			j.user.addActivity(new PrivateChat({
				name	: user.get('username'),
				_id		: activityId,
			}));
			j.user.activities.get(activityId).users.add(user);
		});
		next();

	});

	return null;
});


define('models/network',[
    'backbone'
	], function() {
	var NetworkModel = Backbone.Model.extend({
		
		defaults		: {
			options : {
				pingInterval	: 25000,
				monitorInterval : 2000,
				idleTimeOut			: 10000,
				idleServerDown	: 20000,
				autoStart			: true
			},
			countSent				: 0,
			lastPing				: 0,
			lastPingTime		: 0,
			averagePing			: 0,
			totalWait				: 0,
			serverTime			: 0,
			offsetTime			: 0,
			lastMessageTime : 0,
			serverDown			: 0
		},
		
		initialize : function() {
      this.bind('change:serverDown', this.serverDownChange);
		},
		
		serverDownChange : function() {
			(this.previous('serverDown') === 0) && (Logger.debug('Server down'));
		},
		
		genPing : function() {
			var now = (new Date()).getTime();
			this.set('lastPingTime', now, {silent: true});
			this.set('countSent', this.get('countSent') + 1);
			return {
				localTime	: now,
				countSent	: this.get('countSent')
			};
			
		},
		
		monitor : function() {
			var now = (new Date).getTime();
			if (this.get('lastMessageTime') == 0) {
				this.set('lastMessageTime', now);
			} 
			if(now - this.get('lastMessageTime') > this.get('options').idleServerDown) {
				this.set('serverDown', 1);
				//this.trigger('serverDown');
			} else if(now - this.get('lastMessageTime') > this.get('options').idleTimeOut) {
				this.trigger('requestPing');
			}
		},
		
		processPing : function(message) {
			var now = (new Date()).getTime();
			var diff = now - message.localTime;
			this.set('lastPing', diff, {silent: true});
			this.set('totalWait', this.get('totalWait') + diff, {silent: true});
			this.set('averagePing', this.get('totalWait') / this.get('countSent'), {silent: true});
			this.set('serverTime', message.serverTime - (this.get('lastPing') / 2), {silent: true});
			this.set('offsetTime', this.get('serverTime') - this.get('lastPingTime'));
		}
  });
	
  return NetworkModel;
});
define('controllers/network_controller',[
		'./components/socket_client'
	,	'../models/network'
	, '../models/j'
	], function(SocketClient, NetworkModel, j) {
		j.network = new NetworkModel();
		
		var networkPing = setInterval(function(){
			SocketClient.sendPacket('network.ping', j.network.genPing());
		}, j.network.get('options').pingInterval);
		
		SocketClient.lon('network.ping_result', function(e, message, next) {
			j.network.processPing(message);
			next();
		});
		
		//Monitor the server
		SocketClient.lon('*', function(e, message, next) {
			j.network.set({'lastMessageTime' : (new Date).getTime()});
			j.network.set('serverDown', 0);
			next();
		});
		
		j.network.on('requestPing', function() {
  		Logger.debug('sending network ping');
  		SocketClient.sendPacket('network.ping', j.network.genPing());
		});
		
		var monitor = setInterval(function() {
				j.network.monitor();
		}, j.network.get('options').monitorInterval);
		
		j.lon('ui.network.stop', function(e, messaga, next) {
			clearInterval(networkPing);
			clearInterval(monitor);
		});
		return null;
});

